export const formatDate=timestring=>{const pad=v=>10>v?`0${v}`:v,dateString=timestring.split("T")[0],date=new Date(dateString),weekday=["Sunday","Monday","Tuesday","Wednesday","Thursday","Friday","Saturday"][date.getUTCDay()],day=pad(date.getUTCDate()),month=["January","February","March","April","May","June","July","August","September","October","November","December"][date.getUTCMonth()],year=date.getUTCFullYear();return`${weekday}, ${day} ${month} ${year}`};export const getLink=link=>link?link.replace("content.",""):"";